INSERT INTO `posts` (`PostID`, `Title`, `Category`, `Content`, `Imagelink`, `Conf`, `Status`, `Date`, `UserID`, `Tinycontent`, `Premium`, `Note`) VALUES
(1, 'Chàng trai vào rừng tự xây nhà bằng gỗ và rơm', '1', 'Kris Harbour mua một mảnh đất trong rừng trên eBay sau đó rời bỏ London về đó tự xây lên căn nhà gỗ, trát đất sét trộn rơm với chi phí khoảng 5.000 USD., theo Better Homes and Gardens', 'https://r-cf.bstatic.com/images/hotel/max1024x768/114/114552461.jpg', 10, 1, '2020-07-19', 3, 'Kris Harbour mua một mảnh đất ', 1, 'xcwasc'),
(2, 'Cách hội đồng tuyển sinh đại học Mỹ đánh giá ứng viên', '2', 'Hầu hết học sinh ứng tuyển vào các trường đại học Mỹ đều thắc mắc \"Nhà tuyển sinh mong muốn điều gì ở ứng viên?\". Dưới đây là danh sách những điều các nhà tuyển sinh đại học Mỹ thường không tiết lộ trong lá thư chấp thuận hoặc từ chối ứng viên.', 'data:image/jpeg;base64,/9j/4AAQSkZJRgABAQAAAQABAAD/2wCEAAkGBxISEhUSEhIVFRUXFRUVFRUVFRUVFxcXFRUXFhUVFxUYHSggGBomHRUVITEhJSkrLi4uFx8zODMtNygtLisBCgoKDg0', 5, 0, '2020-06-16', 3, 'Hầu hết học sinh ứng tuyển vào các trường', 0, NULL),
(3, 'Du học sinh trút bỏ nỗi lo bị trục xuất khỏi Mỹ', '2', '16h ngày 14/7 (giờ Mỹ), Nguyễn Thị Thiên Kim cười lớn khi đọc tin Nhà Trắng hủy kế hoạch trục xuất du học sinh nếu học online 100% vào kỳ mùa thu tới.', 'https://megastudy.edu.vn/upload/tinymce/du-hoc-trung-hoc-my-bang-Massachusetts.jpg', 6, 0, '2020-12-07', 3, '16h ngày 14/7 (giờ Mỹ), Nguyễn Thị Thiên Kim ', 0, NULL),
(4, '7 biến thể hít đất', '12', 'Bài hít đất từ dễ đến khó được xây dựng để tăng sức mạnh và cơ bắp toàn thân, cải thiện vóc dáng mà không cần đến phòng tập', 'https://lh3.googleusercontent.com/proxy/vjm6jNAB1200p_ATKZm8oSZSdwMHSdcD-Ov2LcozGTmtaZP_Nfg99037VsIj3Mx9kyy8PNl6g4fY1I1DnNdsUPNeBcmuYCwmMSpdmBo', 22, 0, '2020-07-15', 3, 'Bài hít đất từ dễ đến khó được', 0, NULL),
(5, 'Chàng béo lột xác sau hai năm tập thể hình', '12', 'Tháng 8 tới, Quyền sẽ tham gia giải đấu thể hình toàn quốc. Anh dành toàn bộ thời gian ở phòng tập, siết cơ kết hợp chế độ dinh dưỡng khắt khe để có cơ bắp đẹp nhất', 'https://www.elleman.vn/wp-content/uploads/2018/03/28/t%E1%BA%ADp-h%C3%ADt-%C4%91%E1%BA%A5t-elle-man-feature.jpg', 2, 0, '2020-02-07', 3, 'Tháng 8 tới', 0, NULL),
(6, 'Ưu điểm của công nghệ trồng răng Implant bắt vít', '10', 'Mất răng không chỉ ảnh hưởng đến chức năng ăn nhai mà còn gây mất thẩm mỹ. Hiện nay, có nhiều phương pháp giúp phục hình hàm răng đều cho mọi lứa tuổi, trong đó có trồng răng Implant. Đại diện nha khoa Singae cho biết, nhiều khách hàng có độ tuổi khác nhau, có các bệnh nền như tiểu đường, huyết áp, tim mạch đều lấy lại chức năng ăn nhai tốt nhờ trồng răng Implant', 'https://vinmec-prod.s3.amazonaws.com/images/20190823_001211_019860_tre-bi-sau-rang.max-800x800.jpg', 11, 0, '2020-12-07', 3, 'Mất răng không chỉ ảnh hưởng ', 0, NULL),
(7, 'Phương Anh Idol tiết lộ bí quyết giảm 50 kg', '11', 'Phương Anh giảm được 50 kg, hình thể thon gọn nhờ các chuyên gia Thẩm mỹ viện Ngọc Dung lên liệu trình giảm mỡ riêng và dặn dò mỗi ngày.\r\nMới đây, Phương Anh Idol gây chú ý khi khoe ảnh giảm 50 kg và tự tin diện trang phục ôm dáng. Đa số khán giả khen cô khác biệt, xinh đẹp hơn nhiều.', 'https://cafebiz.cafebizcdn.vn/thumb_w/600/2019/7/18/ngctrinhxxxxxleke-1563468383713667838850-crop-1563468392003748039319.jpg-crop-15634683920037480393', 2, 0, '2020-07-14', 3, 'Phương Anh giảm được 50 kg, hình thể', 0, NULL),
(10, 'Klopp: \'Lampard còn phải học nhiều\'', '3', '<p class=\"description\">HLV Jurgen Klopp bất b&igrave;nh khi đồng nghiệp b&ecirc;n ph&iacute;a Chelsea Frank Lampard tu&ocirc;n ra những nhận x&eacute;t hiềm kh&iacute;ch nhắm v&agrave;o Liverpool.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">\"Lampard đ&atilde; đ&aacute;nh v&agrave;o t&ocirc;i hoặc một thứ g&igrave; đ&oacute; đại loại như vậy. C&aacute;c trợ l&yacute; của t&ocirc;i cũng c&oacute; cảm gi&aacute;c ấy. Bởi ch&uacute;ng t&ocirc;i kh&ocirc;ng hề ki&ecirc;u ngạo\", Klopp n&oacute;i trong buổi họp b&aacute;o trước v&ograve;ng cuối Ngoại hạng Anh. \"Lampard rất gi&agrave;u t&iacute;nh chiến đấu v&agrave; t&ocirc;i ngưỡng mộ điều ấy. Theo quan điểm của t&ocirc;i, bạn c&oacute; thể n&oacute;i thoải m&aacute;i trong những t&igrave;nh huống như vậy. Nhưng sau trận, mọi thứ cần chấm dứt. T&ocirc;i cũng từng n&oacute;i rất nhiều trước đ&acirc;y, dựa tr&ecirc;n cảm x&uacute;c thuần t&uacute;y, v&agrave; Lampard c&oacute; lẽ cũng vậy. &Ocirc;ng ấy đến Anfield để chiến thắng, hoặc ch&iacute; &iacute;t l&agrave; gi&agrave;nh một điểm h&ograve;ng giữ suất dự Champions League. T&ocirc;i t&ocirc;n trọng điều đ&oacute;. Nhưng r&otilde; r&agrave;ng Lampard phải học c&aacute;ch kết th&uacute;c mọi thứ tr&ecirc;n s&acirc;n, sau tiếng c&ograve;i m&atilde;n cuộc\".</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"action_thumb flexbox\">&nbsp;</div>\r\n<div class=\"fig-picture\"><picture data-inimage=\"done\"><source srcset=\"https://i1-thethao.vnecdn.net/2020/07/25/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7_1-8AB3gBqnkSjIgl1Cgw 1x, https://i1-thethao.vnecdn.net/2020/07/25/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=f-1gCM3Xgxu1yH4ud8HJ2A 1.5x, https://i1-thethao.vnecdn.net/2020/07/25/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=jaWlcPKLXHOVxE5_WL6jhw 2x\" data-srcset=\"https://i1-thethao.vnecdn.net/2020/07/25/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7_1-8AB3gBqnkSjIgl1Cgw 1x, https://i1-thethao.vnecdn.net/2020/07/25/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=f-1gCM3Xgxu1yH4ud8HJ2A 1.5x, https://i1-thethao.vnecdn.net/2020/07/25/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=jaWlcPKLXHOVxE5_WL6jhw 2x\" /><img class=\"lazy lazied\" src=\"https://i1-thethao.vnecdn.net/2020/07/25/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7_1-8AB3gBqnkSjIgl1Cgw\" alt=\"Klopp ra dấu cho Lampard b&igrave;nh tĩnh khi c&atilde;i v&atilde; nổ ra tối 22/7. Ảnh: AP.\" loading=\"lazy\" data-src=\"https://i1-thethao.vnecdn.net/2020/07/25/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7_1-8AB3gBqnkSjIgl1Cgw\" data-ll-status=\"loaded\" /></picture>\r\n<div class=\"embed-container\">&nbsp;</div>\r\n</div>\r\n<figcaption>\r\n<p class=\"Image\">Klopp ra dấu cho Lampard b&igrave;nh tĩnh khi c&atilde;i v&atilde; nổ ra tối 22/7. Ảnh:&nbsp;<em>AP.</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Cuối hiệp một trận&nbsp;<a href=\"https://vnexpress.net/liverpool-ha-chelsea-trong-tran-cau-tam-ban-4134600.html\" rel=\"dofollow\">Liverpool gặp Chelsea</a>&nbsp;tại v&ograve;ng 37 Ngoại hạng Anh h&ocirc;m 22/7, Lampard kh&ocirc;ng h&agrave;i l&ograve;ng khi Mateo Kovacic bị thổi phạt sau pha va chạm với Sadio Mane. &Ocirc;ng ph&agrave;n n&agrave;n với trọng t&agrave;i thứ tư, v&agrave; lời qua tiếng lại với một số th&agrave;nh vi&ecirc;n ban huấn luyện Liverpool. Trong l&uacute;c bực tức,&nbsp;<a href=\"https://vnexpress.net/lampard-vang-tuc-truoc-mat-klopp-4135169.html\" rel=\"dofollow\">Lampard tu&ocirc;n ra những từ ngữ tục tĩu</a>&nbsp;v&agrave; cho rằng Liverpool qu&aacute; ki&ecirc;u ngạo d&ugrave; mới một lần đăng quang Ngoại hạng Anh. Đứng ở ph&iacute;a đối diện, Klopp li&ecirc;n tục ra hiệu đề nghị đồng nghiệp b&igrave;nh tĩnh.</p>\r\n</article>', 'https://i.ibb.co/fDPrpbQ/31085588-8556873-Jurgen-Klopp-1600-4953-1595624325.jpg', 0, 0, '0000-00-00', 8, 'HLV Jurgen Klopp bất bình khi đồng nghiệp bên phía Chelsea Frank Lampard tuôn ra những nhận xét hiềm', 1, NULL),
(21, 'Tranh giành 23 sổ đỏ trong vụ án Trầm Bê', '7', '<p class=\"description\">&Ocirc;ng Trầm B&ecirc;, Dương Thanh Cường, Sacombank, Agribank đều muốn d&ugrave;ng hơn 10 ha đất ở huyện B&igrave;nh Ch&aacute;nh (trị gi&aacute; hơn 1.000 tỷ) để khắc phục hậu quả vụ &aacute;n.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Ng&agrave;y 24/7, phi&ecirc;n x&eacute;t xử Trầm B&ecirc; (61 tuổi, cựu Ph&oacute; chủ tịch HĐQT Ng&acirc;n h&agrave;ng Phương Nam, đ&atilde; s&aacute;p nhập v&agrave;o Sacombank) v&agrave; đồng phạm về tội&nbsp;<em>Vi phạm quy định về cho vay trong hoạt động của c&aacute;c tổ chức t&iacute;n dụng</em><em>&nbsp;</em>tiếp tục với phần x&eacute;t hỏi.</p>\r\n<p class=\"Normal\">Trả lời HĐXX, Dương Thanh Cường (nguy&ecirc;n Tổng gi&aacute;m đốc C&ocirc;ng ty CPXD TM B&igrave;nh Ph&aacute;t, chủ mưu trong vụ &aacute;n) cho rằng, mục đ&iacute;ch của m&igrave;nh khi d&ugrave;ng 23 giấy chứng nhận quyền sử dụng đất (10,3 ha) tại huyện B&igrave;nh Ch&aacute;nh<em>&nbsp;</em>l&agrave; nhằm vay vốn đầu tư v&agrave;o bất động sản, chứ kh&ocirc;ng phải để thực hiện dự &aacute;n tr&ecirc;n ch&iacute;nh khu đất n&agrave;y. L&uacute;c mua đất, bị c&aacute;o kh&ocirc;ng biết khu n&agrave;y nằm trong quy hoạch, đến khi được cấp giấy chứng nhận quyền sử dụng đất mới ph&aacute;t hiện.</p>\r\n<p class=\"Normal\">Đến năm 2010, bị c&aacute;o đ&atilde; tất to&aacute;n với Ng&acirc;n h&agrave;ng Phương Nam số tiền l&agrave; 331 tỷ đồng gồm cả gốc v&agrave; l&atilde;i bằng việc g&aacute;n 23 giấy chứng nhận quyền sử dụng đất. Bị c&aacute;o kh&ocirc;ng đồng &yacute; với c&aacute;o buộc g&acirc;y thiệt hại 505 tỷ đồng.</p>\r\n<p class=\"Normal\">Cường cho rằng, gi&aacute; trị của 10,3 ha đất đang thế chấp tại ng&acirc;n h&agrave;ng Phương Nam hiện c&oacute; gi&aacute; hơn 1.000 tỷ đồng. Bị c&aacute;o đề nghị cơ quan chức năng cho ph&eacute;p c&ugrave;ng cơ quan thi h&agrave;nh &aacute;n ph&aacute;t m&atilde;i to&agrave;n bộ khu đất để trả nợ cho Agribank v&agrave; Sacombank.</p>\r\n<p class=\"Normal\">Được x&aacute;c định l&agrave; bị hại trong vụ &aacute;n, đại diện Ng&acirc;n h&agrave;ng Sacombank cho biết, năm 2015, khi Ng&acirc;n h&agrave;ng Phương Nam s&aacute;p nhập c&oacute; tiếp nhận khoản nợ của c&ocirc;ng ty của Dương Thanh Cường. Ph&iacute;a ng&acirc;n h&agrave;ng đồng &yacute; với số tiền thiệt hại được n&ecirc;u trong c&aacute;o trạng, đồng thời y&ecirc;u cầu c&aacute;c bị c&aacute;o phải c&oacute; tr&aacute;ch nhiệm li&ecirc;n đới bồi thường.</p>\r\n<p class=\"Normal\">Đặc biệt, Sacombank cũng đề nghị HĐXX giao 23 giấy chứng nhận quyền sử dụng đất cho ng&acirc;n h&agrave;ng để giải quyết hậu quả của vụ &aacute;n. Đại diện Sacombank xin t&ograve;a xem x&eacute;t giảm nhẹ tr&aacute;ch nhiệm h&igrave;nh sự cho &ocirc;ng B&ecirc; v&agrave; c&aacute;c bị c&aacute;o nguy&ecirc;n l&agrave; nh&acirc;n vi&ecirc;n Ng&acirc;n h&agrave;ng Phương Nam v&igrave; họ phạm tội kh&ocirc;ng v&igrave; động cơ tư lợi...</p>\r\n<p class=\"Normal\">L&agrave; đơn vị&nbsp;<em>c&oacute; quyền, nghĩa vụ li&ecirc;n quan</em>, đại diện Ng&acirc;n h&agrave;ng Agribank kh&ocirc;ng đồng &yacute; giao 23 sổ đỏ cho Sacombank. Đại diện Agribank cho rằng, Dương Thanh Cường đang phải thi h&agrave;nh bản &aacute;n trả cho ng&acirc;n h&agrave;ng hơn 1.000 tỷ đồng, to&agrave; cần giải tỏa k&ecirc; bi&ecirc;n v&agrave; giao 23 sổ đỏ cho Agribank xử l&yacute;.</p>\r\n<p class=\"Normal\">Tại t&ograve;a h&ocirc;m nay, &ocirc;ng Trầm B&ecirc; tiếp tục đề nghị được trả khoản nợ của Cường tại Agribank v&agrave; giao 23 sổ đỏ cho Sacombank để khắc phục hậu quả vụ &aacute;n. \"Nếu được chấp thuận, trong v&ograve;ng 15 ng&agrave;y t&ocirc;i sẽ trả tiền ngay\", &ocirc;ng B&ecirc; n&oacute;i.</p>\r\n<p class=\"Normal\">Trước đ&oacute;, trả lời HĐXX, c&aacute;c bị c&aacute;o nguy&ecirc;n l&agrave; c&aacute;n bộ, nh&acirc;n vi&ecirc;n Ng&acirc;n h&agrave;ng Phương Nam thừa nhận sai phạm trong việc ph&ecirc; duyệt cho c&ocirc;ng ty của Cường vay v&agrave; đề nghị t&ograve;a xem x&eacute;t lại về số tiền thiệt hại.</p>\r\n<p class=\"Normal\">Ri&ecirc;ng bị c&aacute;o&nbsp;<strong>Trầm Viết Trung</strong>&nbsp;(gi&aacute;m đốc trung t&acirc;m x&eacute;t duyệt t&iacute;n dụng) k&ecirc;u oan, cho rằng hợp đồng đầu ti&ecirc;n Ng&acirc;n h&agrave;ng Phương Nam giải ng&acirc;n cho c&ocirc;ng ty của Dương Thanh Cường đ&atilde; thực hiện theo đ&uacute;ng quy định của ph&aacute;p luật, với đầy đủ c&aacute;c điều kiện về t&agrave;i sản bảo đảm v&agrave; quản l&yacute; rủi ro. Hợp đồng n&agrave;y đ&atilde; được tất to&aacute;n, ng&acirc;n h&agrave;ng kh&ocirc;ng xảy ra thiệt hại.</p>\r\n</article>', 'https://i1-vnexpress.vnecdn.net/2020/07/24/tram-be-2913-1595590819.jpg?w=1020&h=0&q=100&dpr=1&fit=crop&s=c0fi-BgmmwEJSu9n76mfSA', 0, 0, '0000-00-00', 8, 'Ông Trầm Bê, Dương Thanh Cường, Sacombank, Agribank đều muốn dùng hơn 10 ha đất ở huyện Bình Chánh (', 1, NULL),
(22, 'Máy bay Concorde – Từ kỳ quan tới thảm họa thương mại', '5', '<p>Nhờ c&ocirc;ng nghệ si&ecirc;u thanh, Concorde từng được giới h&agrave;ng kh&ocirc;ng v&iacute; l&agrave; \"kỳ quan thứ 8\" nhưng những ngợi ca kh&ocirc;ng đủ gi&uacute;p n&oacute; c&oacute; v&ograve;ng đời may mắn.</p>\r\n<p class=\"Normal\">Ng&agrave;y n&agrave;y đ&uacute;ng 20 năm trước, 25/7/2000, một chiếc Concorde rơi ở Paris khiến 113 người tử vong. Nhưng vụ tai nạn chỉ l&agrave; một phần trong chuỗi thảm hoạ của m&aacute;y bay si&ecirc;u thanh thương mại đầu ti&ecirc;n v&agrave; duy nhất của thế giới. Với tốc độ tối đa 2.200km/h, gấp hơn hai lần phi cơ phổ th&ocirc;ng, n&oacute; gi&uacute;p tiết kiệm thời gian đ&aacute;ng kể tr&ecirc;n những đường bay trọng yếu giữa Mỹ v&agrave; ch&acirc;u &Acirc;u.</p>\r\n<p class=\"Normal\"><strong>Lận đận từ khi ra đời</strong></p>\r\n<p class=\"Normal\">Concorde kh&ocirc;ng hề được chế tạo c&oacute; chủ đ&iacute;ch m&agrave; l&agrave; kết quả hợp t&aacute;c bất ngờ giữa Anh v&agrave; Ph&aacute;p. V&agrave;o thập ni&ecirc;n 60, c&ocirc;ng ty BAC của Anh v&agrave; tập đo&agrave;n Ph&aacute;p Sud Aviation đều nghi&ecirc;n cứu chế tạo m&aacute;y si&ecirc;u thanh v&agrave; cả hai đều gặp kh&oacute;. Ph&iacute;a Ph&aacute;p gặp kh&oacute; khi kh&ocirc;ng c&oacute; động cơ đủ mạnh, người Anh lại nắm c&ocirc;ng nghệ động cơ đ&aacute;p ứng được nhu cầu v&agrave; cũng tỏ ra thiện ch&iacute; trước viễn cảnh hai b&ecirc;n c&ugrave;ng sản xuất chung một chiếc phi cơ. Ch&iacute;nh sự li&ecirc;n doanh n&agrave;y g&acirc;y ra một loạt rắc rối sau đ&oacute;.</p>\r\n<figure class=\"tplCaption action_thumb_added\" data-size=\"true\">\r\n<div class=\"fig-picture\"><picture><source srcset=\"https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture2-1360-1595638107.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OD_8nw4MRSx_oZ_hK9NOZw 1x, https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture2-1360-1595638107.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2CQRMCDym5f_774GeJxZkg 1.5x, https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture2-1360-1595638107.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=sBvWJQbEQ_6M1HB62ov1Ww 2x\" data-srcset=\"https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture2-1360-1595638107.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OD_8nw4MRSx_oZ_hK9NOZw 1x, https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture2-1360-1595638107.png?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=2CQRMCDym5f_774GeJxZkg 1.5x, https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture2-1360-1595638107.png?w=680&amp;h=0&amp;q=100&amp;dpr=2&amp;fit=crop&amp;s=sBvWJQbEQ_6M1HB62ov1Ww 2x\" /><img class=\"lazy lazied\" src=\"https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture2-1360-1595638107.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OD_8nw4MRSx_oZ_hK9NOZw\" alt=\"Nguy&ecirc;n mẫu thử nghiệm của Concorde. Ảnh: Wiki.\" loading=\"lazy\" data-src=\"https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture2-1360-1595638107.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=OD_8nw4MRSx_oZ_hK9NOZw\" data-ll-status=\"loaded\" /></picture></div>\r\n<figcaption>\r\n<p class=\"Image\">Nguy&ecirc;n mẫu thử nghiệm của Concorde. Ảnh:&nbsp;<em>Wiki.</em></p>\r\n</figcaption>\r\n</figure>\r\n<p class=\"Normal\">Hai b&ecirc;n sớm xảy ra bất đồng. Một kỹ sư đại diện ph&iacute;a Ph&aacute;p l&agrave; cựu phi c&ocirc;ng l&aacute;i m&aacute;y bay trinh s&aacute;t n&ecirc;n đ&atilde; mắc bệnh nghề nghiệp khi cố gắng đưa v&agrave;o buồng l&aacute;i m&agrave;n h&igrave;nh hiển thị bản đồ di chuyển - thiết bị thiết yếu đối với phi cơ do th&aacute;m. Trong khi Anh kịch liệt phản đối thiết bị n&agrave;y với l&yacute; do Concorde l&agrave; m&aacute;y bay thương mại v&agrave; hoạt động tr&ecirc;n biển l&agrave; chủ yếu.</p>\r\n<p class=\"Normal\">Giai đoạn thử nghiệm ban đầu cũng gặp trở ngại. Chỉ c&oacute; hai buồng l&aacute;i giả lập, một đặt tại Ph&aacute;p, một tại Anh nhưng m&agrave;n h&igrave;nh trong hệ thống ở Anh chất lượng thấp n&ecirc;n kh&ocirc;ng m&ocirc; phỏng được mọi t&igrave;nh huống. Do vậy, trước khi n&oacute; được n&acirc;ng cấp, phi c&ocirc;ng Anh phải \"tập trận\" bằng m&aacute;y bay thật. Điều n&agrave;y rất tốn thời gian v&igrave; phải phụ thuộc thời tiết, cũng như ph&iacute; tiền của, c&ocirc;ng sức do phi c&ocirc;ng cần tập c&aacute;c t&igrave;nh huống phức tạp như hạ c&aacute;nh khi bị hỏng 1 hoặc 2 động cơ. Nhưng sau tất cả c&aacute;c thử nghiệm, Concorde đ&atilde; l&agrave;m n&ecirc;n lịch sử bằng việc trở th&agrave;nh m&aacute;y bay si&ecirc;u thanh thương mại duy nhất trong ng&agrave;nh h&agrave;ng kh&ocirc;ng.</p>', 'https://i1-kinhdoanh.vnecdn.net/2020/07/25/Picture1-2441-1595638107.png?w=0&h=0&q=100&dpr=2&fit=crop&s=mx6y4yzOCl0VecASQhox-Q', 0, 2, '0000-00-00', 8, 'Nhờ công nghệ siêu thanh, Concorde từng được giới hàng không ví là \"kỳ quan thứ 8\" nhưng những ngợi ', 1, NULL),
(23, '95 Sức khỏeTin tứcThứ bảy, 25/7/2020, 11:23 (GMT+7) Người nghi nhiễm ở Đà Nẵng được ghi nhận là \'bệnh nhân 416\'', '1', '<p class=\"description\">Kết quả x&eacute;t nghiệm lần thứ 5 bệnh nh&acirc;n 57 tuổi ở Đ&agrave; Nẵng, được Viện Vệ sinh dịch tễ Trung ương s&aacute;ng nay khẳng định nhiễm nCoV.</p>\r\n<article class=\"fck_detail \">\r\n<p class=\"Normal\">Trong cuộc họp s&aacute;ng nay, quyền Bộ trưởng Y tế Nguyễn Thanh Long cho biết người nghi nhiễm tại Đ&agrave; Nẵng được ghi nhận l&agrave;&nbsp;<strong>\"bệnh nh&acirc;n 416\"</strong>. Viện Vệ sinh Dịch tễ Trung ương đ&atilde; thực hiện x&eacute;t nghiệm chuy&ecirc;n s&acirc;u, kết quả khẳng định dương t&iacute;nh với nCoV s&aacute;ng 25/7.</p>\r\n<p class=\"Normal\">\"Bệnh nh&acirc;n 416\" trước đ&oacute; đ&atilde; 4 lần x&eacute;t nghiệm dương t&iacute;nh nCoV, bao gồm 2 lần x&eacute;t nghiệm tại Đ&agrave; Nẵng, một lần ở Viện Pasteur Nha Trang, lần thứ 4 c&oacute; kết quả v&agrave;o trưa 24/7 do Viện Vệ sinh Dịch tễ Trung ương thực hiện.</p>\r\n<p class=\"Normal\">Như vậy, ca nhiễm mới chấm dứt 99 ng&agrave;y kh&ocirc;ng l&acirc;y nhiễm trong cộng đồng. Hiện chưa r&otilde; nguồn l&acirc;y nhiễm cho người n&agrave;y. Bệnh nh&acirc;n điều trị tại Bệnh viện Đ&agrave; Nẵng&nbsp;<a href=\"https://vnexpress.net/benh-nhan-nghi-nhiem-ncov-o-da-nang-tro-nang-4135698.html\" rel=\"dofollow\">đang trở nặng</a>, vi&ecirc;m phổi cấp t&iacute;nh, diễn biến rất nhanh, phải thở m&aacute;y.&nbsp;<a href=\"https://vnexpress.net/bac-si-cho-ray-mang-may-ecmo-ra-da-nang-dieu-tri-ca-nghi-nhiem-4135824.html\" rel=\"dofollow\">Đội phản ứng nhanh của Bệnh viện Chợ Rẫy</a>&nbsp;đ&atilde; c&oacute; mặt ở Đ&agrave; Nẵng để trực tiếp điều trị. Thủ tướng chỉ đạo quyết t&acirc;m cứu chữa cho bệnh nh&acirc;n.</p>\r\n<p class=\"Normal\">Bệnh nh&acirc;n c&oacute; tiền sử nang trung thất, đ&atilde; được phẫu thuật c&aacute;ch đ&acirc;y khoảng 2 năm. &Ocirc;ng sống tại quận Li&ecirc;n Chiểu, TP Đ&agrave; Nẵng</p>\r\n</article>', 'https://i1-suckhoe.vnecdn.net/2020/07/25/xet-nghiem-Da-Nang1-8431-1595652613.jpg?w=1020&h=0&q=100&dpr=1&fit=crop&s=9H9HBMsOVx--IM1KRzRLGg', 0, 1, '0000-00-00', 8, 'Kết quả xét nghiệm lần thứ 5 bệnh nhân 57 tuổi ở Đà Nẵng, được Viện Vệ sinh dịch tễ Trung ương sáng ', 1, 'tucsdwq'),
